    def start_requests(self):
        # url_list = ['https://www.xiaozhu2.com/beijing/buycar/u2-v3-p48.html']
        # url_list = ["https://www.xiaozhu2.com/buycar/", "https://www.xiaozhu2.com/buycar/u2-v3.html"]
        for url in self.url_list:
            yield scrapy.Request(
                url=url,
                dont_filter=True,
            )

    def parse(self, response):
        # city_list = response.xpath("//div[@class='in']/dl/dd//a/@href").getall()
        # url_list =[]
        # for city in city_list:
        #     sale_url = f'https://www.xiaozhu2.com{city}buycar/'
        #     sald_url = f'https://www.xiaozhu2.com{city}buycar/u2-v3.html'
        #     url_list.append(sale_url)
        #     url_list.append(sald_url)
        # print(url_list)
        url_list = response.xpath("//div[@class='esc-buycar-list']/ul/li/div//a/@href").getall()
        if len(url_list) > 0:
            for url in url_list:
                url = response.urljoin(url)
                if 'u2-v3' not in response.url:
                    status = 'sale'
                else:
                    status = 'sold'
                yield scrapy.Request(
                    url=url,
                    callback=self.parse_detail_url,
                    meta={"status": status}
                )

            next_url = response.xpath("//a[contains(text(),'>')]/@href").get()
            if next_url:
                next_url = response.urljoin(next_url)
                yield scrapy.Request(
                    url=next_url,
                    callback=self.parse,
                    dont_filter=True
                )

    def parse_detail_url(self, response):
        item = GanjiItem()
        status = response.meta["status"]
        item["carid"] = response.xpath('//*[@data-item="carId"]/@value').get()
        item["shortdesc"] = response.xpath("//h1[@class='car-info-title']/text()").get()
        item["price1"] = response.xpath('//*[@data-item="originPrice"]/@value').get()
        item["mileage"] = response.xpath("//dd[contains(text(),'行驶里程')]/preceding-sibling::dt/text()").get()
        item["car_source"] = 'xiaozhu'
        item["grab_time"] = time.strftime("%Y-%m-%d %H:%M:%S", time.localtime())
        item["parsetime"] = item["grab_time"]
        item["pagetitle"] = response.xpath("//title/text()").get()
        item["url"] = response.url
        # item["change_times"] = response.xpath("//li[@class='iNew-hu']//i/text()").get()
        item["brand"] = response.xpath("//div[@class='esc-detail-crubms']/a[3]/text()").get().replace('二手', '')
        item["series"] = response.xpath("//div[@class='esc-detail-crubms']/text()").getall()[1]
        item["city"] = response.xpath("//dd[contains(text(),'所在地')]/preceding-sibling::dt/text()").get()
        # item["registeryear"] = response.xpath("//dd[contains(text(),'首次上牌')]/preceding-sibling::dt/text()").get()
        guidepricetax = response.xpath("//p[contains(text(),'新车指导价')]/text()").get()
        item["guidepricetax"] = re.findall("新车指导价：(.*?)万元", guidepricetax)[0]
        publish_date = response.xpath("//div[contains(text(),'天前发布')]/text()").get().replace("天前发布", "")
        if publish_date:
            item["post_time"] = (datetime.datetime.now()-datetime.timedelta(days=int(publish_date))).strftime("%Y-%m-%d")

        item["yearchecktime"] = response.xpath("//div[contains(text(),'年检到期时间')]/preceding-sibling::div/text()").get()
        item["insurance2_date"] = response.xpath("//div[contains(text(),'保险到期时间')]/preceding-sibling::div/text()").get()
        item["change_times"] = response.xpath("//div[contains(text(),'过户次数')]/preceding-sibling::div/text()").get()
        item["desc"] = response.xpath("//div[contains(text(),'车况描述')]/text()").get()

        item["factoryname"] = response.xpath("//th[contains(text(),'生产厂商')]/following-sibling::td[1]/text()").get()
        item["emission"] = response.xpath("//th[contains(text(),'排放标准')]/following-sibling::td[1]/text()").get()
        item["output"] = response.xpath("//th[contains(text(),'排气量')]/following-sibling::td[1]/text()").get()

        item["modelname"] = response.xpath("//span[contains(text(),'车型名称')]/following-sibling::span[1]/text()").get()
        item["guideprice"] = response.xpath("//span[contains(text(),'厂商指导价')]/following-sibling::span[1]/text()").get()
        # item["factoryname"] = response.xpath("//th[contains(text(),'生产厂商')]/following-sibling::td[1]/text()").get()

        item["pagetime"] = 'zero'
        item["statusplus"] = item["carid"] + '-' + item["price1"]
        item["status"] = status

        description = response.xpath("//meta[@name='description']/@content").get()
        registeryear = re.findall("上牌日期：(.*?)上牌", description)[0]
        if registeryear:
            item["registerdate"] = registeryear
        item["newcarid"] = re.findall('carIds=(.*?)\":', response.text)[0]
        # yield item
        print(item)
